mapper.select:
select B.id as id, B.title as title, B.description as description, B.content as content, U.id as user_id , U.username as username, U.avatar as avatar, B.created_at as created_at, B.updated_at as updated_at from blog B left join user U on B.user_id = U.id
where U.id = #{user_id}
limit #{offset}, #{limit}
